var app = angular.module('gqlpad', []);

app.controller('EditorCtrl', ['$scope', '$http', '$sce', function($scope, $http, $sce) {

	$scope.gql = 'SELECT  -- sample GQL statement\r  number [Number]\r  , short_description [Title]\r  , dv_state [State]\r  , caller_id.email [Caller Email]\r  , caller_id.manager.email [Manager Email]\r  , dv_cmdb_ci [CI]\r  , cmdb_ci\rFROM incident\rWHERE active=true^priority<2^EQ^ORDERBYpriority^ORDERBYDESCnumber\rLIMIT 5,10';

	$scope.formats = [
      {name:'CSV' },
      {name:'HTML'},
      {name:'JSAN'},
      {name:'JSON'},
    ];
	$scope.format = $scope.formats[1];  // HTML

	$scope.run = function() {
		var timer = Date.now();
		$scope.timer = '';
		$scope.result = '';
		$http.get('/x_64935_gql_proc.do', {params: { gql:$scope.gql, format:$scope.format, now:Date.now() }}).success(function(data) {
			$scope.result = $sce.trustAsHtml(typeof data == 'object' ? JSON.stringify(data) : data.replace(/\r\n|\r|\n/g,'<br/>'));
			$scope.timer = $sce.trustAsHtml('took ' + ((Date.now() - timer) / 1e3) + ' seconds');
		});
	}
}]);
